import React from 'react'
import { useState, useEffect } from 'react'
import Spinner from 'react-bootstrap/Spinner'
import Card from 'react-bootstrap/Card'



 const News = () => {
    const [articles, setArticles] = useState(null);
    const [searchWord, setSearchWord] = useState();
    const handleChange = e => {
      setSearchWord(e.target.value);
    };
useEffect(() => {
    fetch(`https://hn.algolia.com/api/v1/search?query=${searchWord}`)
    .then((response) => response.json())
    .then((data) => {
   console.log(data.hits);
    setArticles(data.hits);
})
    .catch((error) => alert('Error'));
}, [searchWord]);



  return (
    <>
    <div>
    <div>
    <input
          type="text"
          placeholder="Search"
          value={searchWord}
          onChange={handleChange}
        />
    </div>


         <h1>Artikel</h1>





      {articles
        ? articles.map((article) => (

<Card class="border border-5 border-design"  style={{ width: '18rem', padding: '20px', margin: '1rem' }}>            
              <div key={article.objectID}>
              <Card.Title><h2 class="text-warning bg-dark" >{article.title}</h2></Card.Title>
              <Card.Text > <p class="text-muted">{article.author}</p></Card.Text>
              <Card.Link><p class="link-style">{article.url}</p></Card.Link>


            </div>
            </Card>
          ))
        : <Spinner animation="border" variant='light' role="status">
        <span className="visually-hidden">Loading...</span>
      </Spinner>}

    </div>
    </>
  );
}
export default News
